import React from "react";

function useJWT() {
  const refreshToken = () => {
    let refresh = localStorage.getItem("refresh");
    const axios = require("axios");
    axios
      .post("http://127.0.0.1:4000/api/token", {
        token: refresh,
      })
      .then((response) => {
        localStorage.setItem("access", JSON.stringify(response.data.access));
        localStorage.setItem("refresh", JSON.stringify(response.data.refresh));
      })
      .catch(function (error) {
        console.log("error:", error);
      });
  };

  async function getData(email, password) {
    const url = "http://127.0.0.1:4000/api/login";
    try {
      const response = await fetch(url, {
        method: "POST",
        body: JSON.stringify({
          email,
          password,
        }),
        headers: {
          "Content-type": "application/json; charset=UTF-8",
        },
      });

      if(!response.ok) {
        throw new Error(`Response Status: ${response.status}`)
      }

      const json = await response.json();
      console.log(json);
    } catch (error) {
      console.log("error:", error.mess)
    }
  }

  function login(email, password) {
    // return new Promise((resolve, reject) => {
    const fetchPromise = fetch("http://127.0.0.1:4000/api/login", {
      method: "POST",
      body: JSON.stringify({
        email,
        password,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    });

    fetchPromise
      .then((response) => response.body)
      .then((body) => {
        const reader = body.getReader();
        console.log("reader:", reader);
        // resolve(data);
        // localStorage.setItem("access", JSON.stringify(data.access));
        // localStorage.setItem("refresh", JSON.stringify(data.refresh));
      })
      .catch((error) => {
        // reject(error);
        console.log("error:", error);
      });
    // });
  }

  const sendPostRequest = (url, data) => {
    const sendRequest = fetch(url, {
      method: "POST",
      body: data,
      headers: {
        "Content-Type": "application/json; charset=UTF-8",
        jwt: "",
      },
    });
  };

  let refresh = localStorage.getItem("refresh");

  const logout = () => {
    const axios = require("axios");
    axios.delete("http://127.0.0.1:4000/api/logout", {
      refresh,
    });

    localStorage.clear();
  };

  return {
    login,
    logout,
    refreshToken,
    sendPostRequest,
  };
}

export default useJWT;
