import React from "react";

function useJWT() {
  const refreshToken = () => {
    let refresh = localStorage.getItem("refresh");
    const axios = require("axios");
    axios
      .post("http://127.0.0.1:4000/api/token", {
        token: refresh,
      })
      .then((response) => {
        localStorage.setItem("access", JSON.stringify(response.data.access));
        localStorage.setItem("refresh", JSON.stringify(response.data.refresh));
      })
      .catch(function (error) {
        console.log("error:", error);
      });
  };

  function login(email, password) {
    if (window.Promise) {
      let promise = new Promise((resolve, reject) => {
        let request = fetch("http://127.0.0.1:4000/api/login", {
          method: "POST",
          body: { email, password },
        })
          .then((resp) => {
            if (resp.ok) {
              localStorage.setItem("access", JSON.stringify(resp.data.access));
              localStorage.setItem(
                "refresh",
                JSON.stringify(resp.data.refresh)
              );
            } else {
              reject(Error(resp));
            }
          })
          .then((json) => {
            resolve(json);
          })
          .catch((error) => {
            console.log(error);
          });
      });
    }

    const axios = require("axios");
    axios
      .post("http://127.0.0.1:4000/api/login", {
        email,
        password,
      })
      .then((response) => {
        localStorage.setItem("access", JSON.stringify(response.data.access));
        localStorage.setItem("refresh", JSON.stringify(response.data.refresh));
      })
      .catch(function (error) {
        console.log("error:", error);
      });
  }

  const sendPostRequest = (url, data) => {};

  let refresh = localStorage.getItem("refresh");

  const logout = () => {
    const axios = require("axios");
    axios.delete("http://127.0.0.1:4000/api/logout", {
      refresh,
    });

    localStorage.clear();
  };

  return {
    login,
    logout,
    refreshToken,
    sendPostRequest,
  };
}

export default useJWT;
