import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Card from "../Components/Card";
import Input from "../Components/Input";
import NotRobot from "../Components/NotRobot";
import LoginButton from "../Components/LoginButtons";
import ErrorMessage from "../Components/ErrorMessage";
import useJWT from "../Hooks/useJWT";

const Login = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login } = useJWT();

  const handleLogin = () => {
    if (email && password) {
      login(email, password);
    }
  };

  const [disable, setDisable] = useState({
    userName: true,
    password: true,
  });

  const handleChangeUserNameInput = (e) => {
    if (e && e.target.value) {
      setDisable((prev) => ({ ...prev, userName: false }));
    }
  };

  const handleChangeUserPassword = (e) => {
    if (e && e.target.value) {
      setDisable((prev) => ({ ...prev, password: false }));
    }
  };

  const handleButtonDisable = () => {
    if (disable.userName && disable.password) {
      return true;
    } else {
      return false;
    }
  };
  console.log("disable:", disable);

  return (
    <Card>
      <h3>Login</h3>
      <Input
        data-testid="email"
        label="✉️ Email"
        type="email"
        defaultValue={email}
        onBlur={(event) => {
          setEmail(event.target.value);
        }}
        autoFocus
        tabIndex="1"
        pattern=".+@example\.com"
        onChange={handleChangeUserNameInput}
      />
      <Input
        data-testid="password"
        label="🔑 Password"
        type="password"
        defaultValue={password}
        onBlur={(e) => {
          setPassword(e.target.value);
        }}
        tabIndex="2"
        onChange={handleChangeUserPassword}
      />

      <NotRobot checked={true} onChange={() => {}} />
      <ErrorMessage />

      <LoginButton
        type="button"
        onClick={handleLogin}
        disabled={handleButtonDisable()}
      />
    </Card>
  );
};

export default Login;
